unction actionUpdate($id=0)	{        if($id != 0 && !UserManager::loginUserIsAdmin() && $id != Yii::app()->user->id)            throw new CHttpException(403,"You are not authorized to perform this action.");		$model=$this->loadModel($id);        $profile=$model->userProfile;		// Uncomment the following line if AJAX validation is needed		// $this->performAjaxValidation($model);		if(isset($_POST['User']))		{			$model->attributes=$_POST['User'];            $profile->attributes=$_POST['UserProfile'];            if($model->validateIgnoreUnsafe()&&$profile->validate())            {                $model->safeUpdate(false);                $profile->save();                Yii::app()->user->updateSession();                Yii::app()->user->setFlash('profileMessage',"Changes is saved.");                $this->redirect(array('/user/profile'));            }		}		$this->render('update',array(			'model'=>$model,            'profile'=>$profile,		));	}	/**	 * Deletes a particular model.	 * If deletion is successful, the browser will be redirected to the 'admin' page.	 * @param integer $id the ID of the model to be deleted	 */	public function actionDelete($id)	{		$this->loadModel($id)->delete();		// if AJAX request (triggered by deletion via admin grid view), we should not redirect the browser		if(!isset($_GET['ajax']))			$this->redirect(isset($_POST['returnUrl']) ? $_POST['returnUrl'] : array('admin'));	}	/**	 * Lists all models.	 */	public function actionIndex()	{		$dataProvider=new CActiveDataProvider('User');		$this->render('index',array(			'dataProvider'=>$dataProvider,		));	}	/**	 * Manages all models.	 */	public function actionAdmin()	{		$model=new User('search');		$model->unsetAttributes();  // clear any default values		if(isset($_GET['User']))			$model->attributes=$_GET['User'];		$this->render('admin',array(			'model'=>$model,		));	}	/**	 * Returns the data model based on the primary key given in the GET variable.	 * If the data model is not found, an HTTP exception will be raised.	 * @param integer $id the ID of the model to be loaded	 * @return User the loaded model	 * @throws CHttpException	 */	public function loadModel($id=0)	{		$model=UserManager::getUser($id);		if($model===null)			throw new CHttpException(404,'The requested page does not exist.');		return $model;	}	/**	 * Performs the AJAX validation.	 * @param User $model the model to be validated	 */	protected function performAjaxValidation($model)	{		if(isset($_POST['ajax']) && $_POST['ajax']==='user-form')		{			echo CActiveForm::validate($model);			Yii::app()->end();		}	}    /* ************************************************************************     *     * f-project.net implementation properties and methods are after this block     *     *********************************************************************** */    /**     * Declares class-based actions.     */    public function actions()    {        return array(            'captcha'=>array(                'class'=>'CCaptchaAction',                'backColor'=>0xFFFFFF,            ),            'registration'=>'application.actions.user.Registration',            'activation'=>'application.actions.user.Activation',            'recovery'=>'application.actions.user.Recovery',            'profile'=>'application.actions.user.Profile',            'changePassword'=>'application.actions.user.ChangePassword',        );    }}