    if ($widgetEdit = $field->widgetEdit($profile))                {                    echo $widgetEdit;                }                elseif ($field->range)                {                    echo $form->dropDownList($profile,$field->varName,Profile::range($field->range));                }                elseif ($field->fieldType=="TEXT")                {                    echo $form->textArea($profile,$field->varName,array('rows'=>6, 'cols'=>50));                }                else                {                    echo $form->textField($profile,$field->varName,array('size'=>60,'maxlength'=>(($field->fieldSize)?$field->fieldSize:255)));                }                echo $form->error($profile,$field->varName); ?>            </div>        <?php        }    }    ?>    <div class="row buttons">        <?php echo CHtml::submitButton($model->isNewRecord ? 'Create' : 'Save'); ?>    </div><?php $this->endWidget(); ?></div><!-- form -->